contracts/interfaces/IChallengePoolCommo.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "../utils/Helpers.sol";

abstract contract IChallengePoolCommon {
    enum ChallengeState {
        open,
        closed,
        cancelled,
        matured,
        evaluated,
        settled,
        disputed
    }
    struct Challenge {
        ChallengeState state;
        bool multi;
        bytes outcome;
        uint256 createdAt;
        uint256 maturity;
        uint256 basePrice;
        address stakeToken;
        ChallengeEvent[] events;
        bytes[] options;
        bool disputed;
        uint256 lastOutcomeSet;
    }

    struct ChallengeEvent {
        bytes params;
        string topicId;
        uint256 maturity;
    }

    error InvalidChallenge();
    error InvalidPrediction();
    error ActionNotAllowedForState(ChallengeState _state);
    error PlayerDidNotWinPool();
    error PlayerNotInPool();
    error PlayerAlreadyWithdrawn();
    error StakeLowerThanMinimum();
    error InvalidEventTopic();
    error InvalidEventParam();
    error InvalidEventMaturity();
    error InvalidEventLength();
    error InvalidPoolOption();
    error InvalidOutcome();
    error UnsupportedToken(address _token);
    error MaxPriceExceeded();
    error DeadlineExceeded();
    error BelowMinPrie();
    error InsufficientStakes(uint256 _requested, uint256 _available);
    error DisputePeriodElapsed();
    error PlayerAlreadyDisputed();
    error PlayerAlreadyReleased();
    error PlayerDidNotDispute();
    error PlayerLostDispute();
    error DisputePeriod();
}

contracts/interfaces/IChallengePoolDispu.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;
import "./IChallengePoolCommon.sol";

abstract contract IChallengePoolDispute is  IChallengePoolCommon {
    struct Dispute {
        bytes dispute;
        uint256 stake;
        bool released;
    }
    event DisputeOutcome(
        uint256 challengeId,
        address disputor,
        ChallengeState state,
        bytes result,
        uint256 amount
    );
    event DisputeReleased(
        uint256 challengeId,
        address disputor,
        ChallengeState state,
        bytes result,
        uint256 amount
    );
    event SettleDispute(
        uint256 challengeId,
        address disputor,
        ChallengeState state,
        bytes result,
        uint256 amount
    );
    event CancelChallenge(
        uint256 challengeId,
        address canceller,
        ChallengeState state
    );

    /**
     * @notice  .
     * @dev     anyone can call this to dispute the outcome.
     * @param   _challengeId  .
     * @param   _outcome  their suggested outcome.
     */
    function dispute(
        uint256 _challengeId,
        bytes calldata _outcome
    ) external virtual;
    function releaseDispute(uint256 _challengeId) external virtual;
    /**
     * @notice  .
     * @dev      dispute admin can call this to settle dispute.
     * @param   _challengeId  .
     * @param   _outcome  decoded and applied based on the event topic type.
     */
    function settle(
        uint256 _challengeId,
        bytes calldata _outcome
    ) external virtual;
    /**
     * @notice  .
     * @dev     cancel pool for some reason
     * @param   _challengeId  .
     */
    function cancel(uint256 _challengeId) external virtual;
}

contracts/interfaces/IChallengePoolHandl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;
import "./IChallengePoolCommon.sol";
abstract contract IChallengePoolHandler is IChallengePoolCommon {
    struct Supply {
        uint256 stakes;
        uint256 tokens;
    }
    struct OptionSupply {
        bool exists;
        uint256 stakes;
        uint256 tokens;
        uint256 rewards;
    }
    struct PlayerSupply {
        bool withdrawn;
        uint256 stakes;
        uint256 tokens;
        uint256 rewards;
    }

    event NewChallenge(
        uint256 challengeId,
        address creator,
        uint256 createdAt,
        uint256 maturity,
        ChallengeState state,
        bytes result,
        uint256 basePrice,
        uint256 fee,
        uint256 quantity,
        uint256 totalAmount,
        uint256 rewards,
        bytes prediction,
        ChallengeEvent[] events,
        bytes[] options,
        address stakeToken,
        bool multi
    );
    event Stake(
        uint256 challengeId,
        address participant,
        bytes option,
        uint256 stakes,
        uint256 price,
        uint256 totalAmount,
        uint256 fee,
        uint256 rewards
    );
    event Withdraw(
        uint256 challengeId,
        address participant,
        bytes option,
        uint256 stakes,
        uint256 price,
        uint256 penalty,
        uint256 totalAmount,
        uint256 fee,
        uint256 rewards
    );
    event EvaluateChallenge(
        uint256 challengeId,
        address evaluator,
        ChallengeState state,
        bytes result
    );
    event CloseChallenge(
        uint256 challengeId,
        address closer,
        ChallengeState state,
        bytes result
    );

    event WinningsWithdrawn(
        uint256 challengeId,
        address participant,
        uint256 amountWon,
        uint256 amountWithdrawn
    );
    /**
     * @notice  .
     * @dev     create a new challenge
     * @param   _events  events in the challenge, maximum of one event if _options is not empty
     * @param   _options  challenge options, if empty then it is a yes or no pool
     * @param   _stakeToken  token used for staking on this challenge
     * @param   _prediction  prediction of user creating the challenge
     * @param   _quantity  how many stakes user is purchasing for this prediction
     * @param   _basePrice  the base price of a stake. total amount to be transferred is _basePrice * _quantity
     * @param   _paymaster  a contract the pays the total amount on behalf of the user set to 0x if none
     */
    function createChallenge(
        ChallengeEvent[] calldata _events,
        bytes[] calldata _options,
        address _stakeToken,
        bytes calldata _prediction,
        uint256 _quantity,
        uint256 _basePrice,
        address _paymaster
    ) external virtual;
    /**
     * @notice  .
     * @dev     stake on a pool
     * @param   _challengeId  .
     * @param   _prediction  .
     * @param   _quantity  how many stakes user is purchasing for this prediction
     * @param   _paymaster  a contract the pays the total amount on behalf of the user set to 0x if none
     */
    function stake(
        uint256 _challengeId,
        bytes calldata _prediction,
        uint256 _quantity,
        address _paymaster
    ) external virtual;

    /**
     * @notice  .
     * @dev     early withdrawal allows player to get out of their stake. price of option calculated accordingly and applied
     * @param   _challengeId  .
     * @param   _option  option to withdraw from
     * @param   _quantity  of stakes to withdraw.
     * @param   _deadline  time after which this early withdraw transaction will revert
     */
    function earlyWithdraw(
        uint256 _challengeId,
        bytes calldata _option,
        uint256 _quantity,
        uint256 _deadline
    ) external virtual;

    /**
     * @notice  .
     * @dev     withdraw winnings from pool, reverts if user lost
     * @param   _challengeId  .
     */
    function withdraw(uint256 _challengeId) external virtual;
    /**
     * @notice  .
     * @dev     bulk withdrawal
     * @param   _challengeIds  .
     */
    function bulkWithdraw(uint256[] calldata _challengeIds) external virtual;

    /**
     * @notice  .
     * @dev     evaluate pool once it's matured
     * @param   _challengeId  .
     */
    function evaluate(uint256 _challengeId) external virtual;
    /**
     * @notice  .
     * @dev     close pool once it's evaluated or settled
     * @param   _challengeId  .
     */
    function close(uint256 _challengeId) external virtual;
}

contracts/interfaces/IChallengePoolManag.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

interface IChallengePoolManager {
    struct StakeToken {
        address token;
        uint256 accumulatedFee;
        bool active;
    }

    event StakeTokenAdded(
        address  caller,
        address  token,
        bool active
    );
    event StakeTokenRemoved(
        address  caller,
        address  token,
        bool active
    );
    event FeeWithdrawn(
        address  caller,
        address  token,
        address to,
        uint256 amount
    );
    event SetFeeAddress(
        address  caller,
        address oldFeeAddress,
        address newFeeAddress
    );
    event SetMinMaturityPeriod(
        address  caller,
        uint256 oldMinMaturityPeriod,
        uint256 newMinMaturityPeriod
    );
    event SetCreatePoolFee(
        address  caller,
        uint256 oldCreatePoolFee,
        uint256 newCreatePoolFee
    );
    event SetStakeFee(
        address  caller,
        uint256 oldStakeFee,
        uint256 newStakeFee
    );
    event SetEarlyWithdrawFee(
        address  caller,
        uint256 oldEarlyWithdrawFee,
        uint256 newEarlyWithdrawFee
    );
    event SetMaxOptionsPerPool(
        address  caller,
        uint256 oldMaxOptionsPerPool,
        uint256 newMaxOptionsPerPool
    );
    event SetMaxEventsPerPool(
        address  caller,
        uint256 oldMaxEventsPerPool,
        uint256 newMaxEventsPerPool
    );
    event SetMinStakeAmount(
        address  caller,
        uint256 oldMinStakeAmount,
        uint256 newMinStakeAmount
    );
    event SetDisputePeriod(
        address  caller,
        uint256 oldDisputePeriod,
        uint256 newDisputePeriod
    );
    event SetDisputeStake(
        address  caller,
        uint256 oldDisputeStake,
        uint256 newDisputeStake
    );
    event SetStakeAirDrop(
        address  caller,
        uint256 oldStakeAirDrop,
        uint256 newStakeAirDrop
    );
    event SetMaxClaim(
        address  caller,
        uint256 oldMaxClaim,
        uint256 newMaxClaim
    );
    event SetPaymaster(
        address  caller,
        address oldPaymaster,
        address newPaymaster
    );

    function setFeeAddress(address _feeAddress) external;

    function setMinMaturityPeriod(uint256 _minMaturityPeriod) external;

    function setCreatePoolFee(uint256 _createPoolFee) external;

    function setStakeFee(uint256 _stakeFee) external;

    function setEarlyWithdrawFee(uint256 _earlyWithdrawFee) external;

    function setMaxOptionsPerPool(uint256 _maxOptionsPerPool) external;

    function setMaxEventsPerPool(uint256 _maxEventsPerPool) external;

    function setMinStakeAmount(uint256 _minStakeAmount) external;

    function setDisputePeriod(uint256 _disputePeriod) external;

    function setDisputeStake(uint256 _disputeStake) external;

    function addStakeToken(address _stakeToken) external;

    function removeStakeToken(address _stakeToken) external;

    function withdrawFee(address _stakeToken) external;

    function feeAddress() external view returns (address);

    function minMaturityPeriod() external view returns (uint256);

    function createPoolFee() external view returns (uint256);

    function stakeFee() external view returns (uint256);

    function earlyWithdrawFee() external view returns (uint256);

    function maxOptionsPerPool() external view returns (uint256);

    function maxEventsPerPool() external view returns (uint256);

    function minStakeAmount() external view returns (uint256);

    function disputePeriod() external view returns (uint256);

    function disputeStake() external view returns (uint256);

    function challengeId() external view returns(uint256);

    function stakeToken(
        address _token
    ) external view returns (StakeToken memory);

    function setStakeAirDrop(uint256 _stakeAirDrop) external;
    function setMaxClaim(uint256 _maxClaim) external;
    function setPaymaster(address _paymaster) external;
}

contracts/interfaces/IChallengePoolView..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;
import "./IChallengePoolHandler.sol";
import "./IChallengePoolDispute.sol";
import "./IDataProvider.sol";
interface IChallengePoolView {
    function challenges(
        uint256 _challengeId
    ) external view returns (IChallengePoolHandler.Challenge memory);

    function playerOptionSupply(
        uint256 _challengeId,
        address _player,
        bytes32 _option
    ) external view returns (IChallengePoolHandler.PlayerSupply memory);
    function playerSupply(
        uint256 _challengeId,
        address _player
    ) external view returns (IChallengePoolHandler.PlayerSupply memory);
    function optionSupply(
        uint256 _challengeId,
        bytes32 _option
    ) external view returns (IChallengePoolHandler.OptionSupply memory);
    function poolSupply(
        uint256 _challengeId
    ) external view returns (IChallengePoolHandler.Supply memory);
    function playerDisputes(
        uint256 _challengeId,
        address _player
    ) external view returns (IChallengePoolDispute.Dispute memory);
    function optionDisputes(
        uint256 _challengeId,
        bytes32 _option
    ) external view returns (uint256);
    function poolDisputes(uint256 _challengeId) external view returns (uint256);

    function dataRequest(
        bytes32 _requestId
    ) external view returns (IDataProvider.DataRequest memory);
    function requestOptions(
        bytes32 _requestId,
        bytes32 _option
    ) external view returns (bool);
    /**
     * @notice  .
     * @dev     price calculation for a pool option
     * @param   _challengeId  .
     * @return  uint256  .
     */
    function price(uint256 _challengeId) external view returns (uint256);

    function earlyWithdrawPenalty(
        uint256 _challengeId
    ) external view returns (uint256 penalty, uint256 priceMinusPenalty);

    function earlyWithdrawFee(
        uint256 _price
    ) external view returns (uint256 fee, uint256 feePlusPrice);

    function createFee(
        uint256 _price
    ) external view returns (uint256 fee, uint256 feePlusPrice);

    function stakeFee(
        uint256 _price
    ) external view returns (uint256 fee, uint256 feePlusPrice);
    function winnerShare(
        uint256 _challengeId,
        address _player
    ) external view returns (uint256);

    function stakeAirDrop() external view returns (uint256);
    function maxClaim() external view returns (uint256);
    function paymaster() external view returns (address);
}

